import { useState, useEffect } from 'react';
import axios from 'axios';
import './PastContests.css'; // Import the CSS file for styling

const PastContests = () => {
  const [pastContests, setPastContests] = useState([]);
  const [bookmarkedContests, setBookmarkedContests] = useState([]);

  useEffect(() => {
    const fetchPastContests = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/contests/past');
        setPastContests(response.data.slice(0, 20)); // Limit to 20 past contests
      } catch (error) {
        console.error('Error fetching past contests:', error);
      }
    };

    fetchPastContests();
  }, []);

  const toggleBookmark = (contestId) => {
    setBookmarkedContests((prevBookmarks) =>
      prevBookmarks.includes(contestId)
        ? prevBookmarks.filter((id) => id !== contestId)
        : [...prevBookmarks, contestId]
    );
  };

  return (
    <div className="past-contests-container">
      <h2 className="past-contests-title">Past Contests</h2>
      <ul className="past-contests-list">
        {pastContests.map((contest) => (
          <li
            key={contest.id}
            className={`past-contest-item ${
              bookmarkedContests.includes(contest.id) ? 'bookmarked' : ''
            }`}
          >
            <div className="contest-details">
              <a
                href={contest.link}
                target="_blank"
                rel="noopener noreferrer"
                className="past-contest-link"
              >
                {contest.name}
              </a>
              <span className="past-contest-date">
                {new Date(contest.startTime).toLocaleString()}
              </span>
              <span className="past-contest-duration">
                Duration: {contest.duration} hours
              </span>
              <span className="past-contest-problems">
                Problems: {contest.problemCount}
              </span>
              <span className="past-contest-difficulty">
                Difficulty: {contest.difficultyLevel}
              </span>
            </div>
            <button
              className="bookmark-button"
              onClick={() => toggleBookmark(contest.id)}
            >
              {bookmarkedContests.includes(contest.id) ? 'Unbookmark' : 'Bookmark'}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PastContests;
