import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './PastContests..css'; // Import the CSS Module

const PastContests = () => {
  const [pastContests, setPastContests] = useState([]);

  useEffect(() => {
    const fetchContests = async () => {
      try {
        const response = await axios.get('https://codeforces.com/api/contest.list');
        const contests = response.data.result;
        const currentTime = Math.floor(Date.now() / 1000);

        // Filter past contests and map to desired format
        const pastContestsData = contests
          .filter(contest => contest.startTimeSeconds < currentTime)
          .slice(0, 20) // Limit to 20 past contests
          .map(contest => ({
            id: contest.id,
            name: contest.name,
            startTime: new Date(contest.startTimeSeconds * 1000),
            duration: contest.durationSeconds / 3600, // Convert duration to hours
            link: `https://codeforces.com/contest/${contest.id}`
          }));

        setPastContests(pastContestsData);
      } catch (error) {
        console.error('Error fetching contests:', error);
      }
    };

    fetchContests();
  }, []);

  return (
    <div className={styles.pastContestsContainer}>
      <h2 className={styles.heading}>Past Contests</h2>
      <ul className={styles.contestList}>
        {pastContests.map(contest => (
          <li key={contest.id} className={styles.contestItem}>
            <a
              href={contest.link}
              target="_blank"
              rel="noopener noreferrer"
              className={styles.contestLink}
            >
              {contest.name}
            </a>
            <p className={styles.contestDetails}>Start Time: {contest.startTime.toLocaleString()}</p>
            <p className={styles.contestDetails}>Duration: {contest.duration} hours</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PastContests;
