import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Row, Col } from 'react-bootstrap';

const PastContests = () => {
  const [pastContests, setPastContests] = useState([]);

  useEffect(() => {
    const fetchContests = async () => {
      try {
        const response = await axios.get('https://codeforces.com/api/contest.list');
        const contests = response.data.result;
        const currentTime = Math.floor(Date.now() / 1000);

        const pastContestsData = contests
          .filter(contest => contest.startTimeSeconds < currentTime)
          .slice(0, 20)
          .map(contest => ({
            id: contest.id,
            name: contest.name,
            startTime: new Date(contest.startTimeSeconds * 1000),
            duration: contest.durationSeconds / 3600,
            link: `https://codeforces.com/contest/${contest.id}`
          }));

        setPastContests(pastContestsData);
      } catch (error) {
        console.error('Error fetching contests:', error);
      }
    };

    fetchContests();
  }, []);

  return (
    <div className="container mt-4">
      <h2>Past Contests</h2>
      <Row xs={1} md={2} lg={3} className="g-4">
        {pastContests.map(contest => (
          <Col key={contest.id}>
            <Card>
              <Card.Body>
                <Card.Title>{contest.name}</Card.Title>
                <Card.Text>
                  <strong>Start Time:</strong> {contest.startTime.toLocaleString()}
                </Card.Text>
                <Card.Text>
                  <strong>Duration:</strong> {contest.duration} hours
                </Card.Text>
                <a href={contest.link} target="_blank" rel="noopener noreferrer" className="btn btn-primary">
                  View Contest
                </a>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default PastContests;
