import { useEffect, useState } from "react";
import axios from "axios";

function Home() {
    const [contests, setContests] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/api/contests/fetch')
            .then(response => {
                if (Array.isArray(response.data)) {
                    setContests(response.data);
                } else {
                    console.error('API response is not an array:', response.data);
                }
            })
            .catch(error => {
                if (error.response) {
                    console.error('Response error:', error.response.status, error.response.data);
                } else if (error.request) {
                    console.error('No response received:', error.request);
                } else {
                    console.error('Error:', error.message);
                }
            });
    }, []);

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-xl font-bold">Upcoming Contests</h1>
            {Array.isArray(contests) && contests.filter(c => !c.past).map(contest => (
                <div key={contest._id} className="p-4 border rounded my-2">
                    <h2 className="text-lg">{contest.name} ({contest.platform})</h2>
                    <p>Starts at: {new Date(contest.startTime).toLocaleString()}</p>
                    <a href={contest.link} target="_blank" rel="noopener noreferrer">Join Contest</a>
                </div>
            ))}
        </div>
    );
}

export default Home;
