import { useEffect, useState } from "react";
import axios from "axios";

function Home() {
    const [contests, setContests] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/api/contests/upcoming')
            .then(response => setContests(response.data))
            .catch(error => {
                if (error.response) {
                    console.error('Response error:', error.response.status, error.response.data);
                } else if (error.request) {
                    console.error('No response received:', error.request);
                } else {
                    console.error('Error:', error.message);
                }
            });
    }, []);

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4 text-center">Upcoming Contests</h1>
            <div className="grid gap-4 sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3">
                {Array.isArray(contests) && contests.filter(c => !c.past).map(contest => (
                    <div key={contest._id} className="p-4 border rounded shadow-md hover:shadow-lg transition-shadow duration-300">
                        <h2 className="text-lg font-semibold mb-2">{contest.name} ({contest.platform})</h2>
                        <p className="text-gray-600 mb-4">Starts at: {new Date(contest.startTime).toLocaleString()}</p>
                        <a href={contest.link} target="_blank" rel="noopener noreferrer" className="inline-block bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition-colors duration-300">Join Contest</a>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Home;
