import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Home.css'; // Import the CSS file for additional styling

function Home() {
  const [contests, setContests] = useState([]);
  const [bookmarkedContests, setBookmarkedContests] = useState([]);

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/contests/upcoming')
      .then((response) => setContests(response.data))
      .catch((error) => {
        if (error.response) {
          console.error('Response error:', error.response.status, error.response.data);
        } else if (error.request) {
          console.error('No response received:', error.request);
        } else {
          console.error('Error:', error.message);
        }
      });
  }, []);

  const toggleBookmark = (contestId) => {
    setBookmarkedContests((prevBookmarks) =>
      prevBookmarks.includes(contestId)
        ? prevBookmarks.filter((id) => id !== contestId)
        : [...prevBookmarks, contestId]
    );
  };

  return (
    <div className="container">
      <h1 className="title">Upcoming Contests</h1>
      <div className="contest-grid">
        {Array.isArray(contests) &&
          contests
            .filter((c) => !c.past)
            .map((contest) => (
              <div
                key={contest._id}
                className={`contest-card ${
                  bookmarkedContests.includes(contest._id) ? 'bookmarked' : ''
                }`}
              >
                <h2 className="contest-name">
                  {contest.name} ({contest.platform})
                </h2>
                <p className="contest-info">
                  Starts at: {new Date(contest.startTime).toLocaleString()}
                </p>
                <a
                  href={contest.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="join-button"
                >
                  Join Contest
                </a>
                <button
                  onClick={() => toggleBookmark(contest._id)}
                  className="bookmark-button"
                >
                  {bookmarkedContests.includes(contest._id) ? 'Remove Bookmark' : 'Bookmark'}
                </button>
              </div>
            ))}
      </div>
    </div>
  );
}

export default Home;
